        [4316] = {7312,10,1,"刘备",202,0,"大丈夫抱经世奇才，岂可空老于林泉之下？愿先生以天下苍生为念，开备愚鲁而赐教。","大丈夫抱经世奇才，岂可空老于林泉之下？愿先生以天下苍生为念，开备愚鲁而赐教。",1,"story_lb2_228","story_lb2_228","诸葛亮",203,},
        [4317] = {7312,11,1,"刘备",202,0,"大丈夫抱经世奇才，岂可空老于林泉之下？愿先生以天下苍生为念，开备愚鲁而赐教。","大丈夫抱经世奇才，岂可空老于林泉之下？愿先生以天下苍生为念，开备愚鲁而赐教。",1,"story_lb2_229","story_lb2_229","诸葛亮",203,},
