        [4427] = {7601,2,1,"曹操",103,0,"孤命汝为都督，于禁、李典、夏侯兰、韩浩为副将，领兵十万，直抵博望城，以窥新野。","孤命汝为都督，于禁、李典、夏侯兰、韩浩为副将，领兵十万，直抵博望城，以窥新野。",1,"story_cc1_485","story_cc1_485","夏侯惇",105,},
