        [4432] = {7601,7,2,"徐庶",212,0,"亮字孔明，道号卧龙先生。有经天纬地之才，出鬼入神之计，真当世之奇才，非可小觑。","亮字孔明，道号卧龙先生。有经天纬地之才，出鬼入神之计，真当世之奇才，非可小觑。",1,"story_xs1_70","story_xs1_70","曹操",103,},
