        [4439] = {7602,1,1,"刘备",202,0,"曹操差夏侯惇引兵十万，杀奔新野来了。如何迎敌？","曹操差夏侯惇引兵十万，杀奔新野来了。如何迎敌？",1,"story_lb2_249","story_lb2_249","张飞",206,},
