        [4449] = {7604,3,1,"诸葛亮",203,0,"博望之左有山，名曰豫山。右有林，名曰安林，可以埋伏军马。","博望之左有山，名曰豫山。右有林，名曰安林，可以埋伏军马。",1,"story_zgl_66","story_zgl_66","张飞",206,},
        [4450] = {7604,4,1,"诸葛亮",203,0,"云长可引一千军往豫山埋伏，等彼军至，放过休敌，其辎重粮草，必在后面，但看南面火起，可纵兵出击，就焚其粮草。","云长可引一千军往豫山埋伏，等彼军至，放过休敌，其辎重粮草，必在后面，但看南面火起，可纵兵出击，就焚其粮草。",1,"story_zgl_67","story_zgl_67","张飞",206,},
        [4451] = {7604,5,1,"诸葛亮",203,0,"云长可引一千军往豫山埋伏，等彼军至，放过休敌，其辎重粮草，必在后面，但看南面火起，可纵兵出击，就焚其粮草。","云长可引一千军往豫山埋伏，等彼军至，放过休敌，其辎重粮草，必在后面，但看南面火起，可纵兵出击，就焚其粮草。",1,"story_zgl_68","story_zgl_68","张飞",206,},
        [4452] = {7604,6,1,"诸葛亮",203,0,"翼德可引一千军去安林背后山谷中埋伏，只看南面火起，便可出，向博望城旧屯粮草处纵火烧之。","翼德可引一千军去安林背后山谷中埋伏，只看南面火起，便可出，向博望城旧屯粮草处纵火烧之。",1,"story_zgl_69","story_zgl_69","张飞",206,},
        [4453] = {7604,7,1,"诸葛亮",203,0,"翼德可引一千军去安林背后山谷中埋伏，只看南面火起，便可出，向博望城旧屯粮草处纵火烧之。","翼德可引一千军去安林背后山谷中埋伏，只看南面火起，便可出，向博望城旧屯粮草处纵火烧之。",1,"story_zgl_70","story_zgl_70","张飞",206,},
        [4454] = {7604,8,1,"诸葛亮",203,0,"关平、刘封可引五百军，预备引火之物，于博望坡后两边等候，至初更兵到，便可放火矣。","关平、刘封可引五百军，预备引火之物，于博望坡后两边等候，至初更兵到，便可放火矣。",1,"story_zgl_71","story_zgl_71","张飞",206,},
