        [4464] = {7604,18,1,"诸葛亮",203,0,"主公今日可便引兵就博望山下屯住。来日黄昏，敌军必到，主公便弃营而走，但见火起，即回军掩杀。","主公今日可便引兵就博望山下屯住。来日黄昏，敌军必到，主公便弃营而走，但见火起，即回军掩杀。",1,"story_zgl_76","story_zgl_76","关羽",205,},
