        [4467] = {7605,1,2,"夏侯惇",105,0,"徐元直在丞相面前，夸诸葛亮为天人，今观其用兵，乃以此等军马为前部，与吾对敌，正如驱犬羊与虎豹斗耳！","徐元直在丞相面前，夸诸葛亮为天人，今观其用兵，乃以此等军马为前部，与吾对敌，正如驱犬羊与虎豹斗耳！",1,"story_xhd_47","story_xhd_47","",0,},
        [4468] = {7605,2,2,"夏侯惇",105,0,"徐元直在丞相面前，夸诸葛亮为天人，今观其用兵，乃以此等军马为前部，与吾对敌，正如驱犬羊与虎豹斗耳！","徐元直在丞相面前，夸诸葛亮为天人，今观其用兵，乃以此等军马为前部，与吾对敌，正如驱犬羊与虎豹斗耳！",1,"story_xhd_48","story_xhd_48","",0,},
