        [4500] = {7701,6,1,"曹操",103,0,"吾所虑者，刘备、孙权耳，余皆不足介意，今当乘此时扫平江南。","吾所虑者，刘备、孙权耳，余皆不足介意，今当乘此时扫平江南。",1,"story_cc1_491","story_cc1_491","夏侯惇",105,},
        [4501] = {7701,7,1,"曹操",103,0,"起大兵五十万，曹仁、曹洪为第一队，张辽、张郃为第二队。","起大兵五十万，曹仁、曹洪为第一队，张辽、张郃为第二队。",1,"story_cc1_492","story_cc1_492","夏侯惇",105,},
        [4502] = {7701,8,1,"曹操",103,0,"夏侯渊、夏侯惇为第三队，于禁、李典为第四队，孤自领诸将为第五队，每队各引兵十万。","夏侯渊、夏侯惇为第三队，于禁、李典为第四队，孤自领诸将为第五队，每队各引兵十万。",1,"story_cc1_493","story_cc1_493","夏侯惇",105,},
        [4503] = {7701,9,1,"曹操",103,0,"许褚为折冲将军，引兵三千为先锋，选定建安十三年秋七月丙午日出师。","许褚为折冲将军，引兵三千为先锋，选定建安十三年秋七月丙午日出师。",1,"story_cc1_494","story_cc1_494","夏侯惇",105,},
        [4504] = {7702,1,2,"孔融",417,0,"刘备，刘表皆汉室宗亲，不可轻伐；孙权虎踞六郡，且有大江之险，亦不易取，今丞相兴此无义之师，恐失天下之望。","刘备，刘表皆汉室宗亲，不可轻伐；孙权虎踞六郡，且有大江之险，亦不易取，今丞相兴此无义之师，恐失天下之望。",1,"story_kr_24","story_kr_24","曹操",103,},
        [4505] = {7702,2,2,"孔融",417,0,"刘备，刘表皆汉室宗亲，不可轻伐；孙权虎踞六郡，且有大江之险，亦不易取，今丞相兴此无义之师，恐失天下之望。","刘备，刘表皆汉室宗亲，不可轻伐；孙权虎踞六郡，且有大江之险，亦不易取，今丞相兴此无义之师，恐失天下之望。",1,"story_kr_25","story_kr_25","曹操",103,},
