        [4510] = {7704,1,2,"郗虑",131,0,"融平日每每狎侮丞相，又与祢衡相善，衡赞融曰仲尼不死，融赞衡曰颜回复生。","融平日每每狎侮丞相，又与祢衡相善，衡赞融曰仲尼不死，融赞衡曰颜回复生。",1,"story_cl_1","story_cl_1","曹操",103,},
        [4511] = {7704,2,2,"郗虑",131,0,"向者祢衡之辱丞相，乃融使之也。","向者祢衡之辱丞相，乃融使之也。",1,"story_cl_2","story_cl_2","曹操",103,},
        [4512] = {7704,3,2,"郗虑",131,0,"昨日出丞相府，曾言：以至不仁伐至仁，安得不败乎！","昨日出丞相府，曾言：以至不仁伐至仁，安得不败乎！",1,"story_cl_3","story_cl_3","曹操",103,},
