        [4527] = {7804,1,1,"刘琮",223,0,"吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主。倘兄与叔兴兵问罪，如何解释？","吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主。倘兄与叔兴兵问罪，如何解释？",1,"story_lz2_3","story_lz2_3","",999,},
        [4528] = {7804,2,1,"刘琮",223,0,"吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主。倘兄与叔兴兵问罪，如何解释？","吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主。倘兄与叔兴兵问罪，如何解释？",1,"story_lz2_4","story_lz2_4","",999,},
        [4529] = {7804,3,2,"李珪",133,0,"公子之言甚善。今可急发哀书至江夏，请大公子为荆州之主，就命玄德一同理事。","公子之言甚善。今可急发哀书至江夏，请大公子为荆州之主，就命玄德一同理事。",1,"story_lg2_5","story_lg2_5","刘琮",223,},
        [4530] = {7804,4,2,"李珪",133,0,"北可以敌曹操，南可以拒孙权。此万全之策也。","北可以敌曹操，南可以拒孙权。此万全之策也。",1,"story_lg2_6","story_lg2_6","刘琮",223,},
        [4531] = {7804,5,1,"蔡瑁",443,0,"汝何人，敢乱言以逆主公遗命！","汝何人，敢乱言以逆主公遗命！",1,"story_cm_36","story_cm_36","李珪",133,},
        [4532] = {7804,6,2,"李珪",133,0,"汝内外朋谋，假称遗命，废长立幼，眼见荆襄九郡，送于蔡氏之手！故主有灵，必当殛汝！","汝内外朋谋，假称遗命，废长立幼，眼见荆襄九郡，送于蔡氏之手！故主有灵，必当殛汝！",1,"story_lg2_7","story_lg2_7","蔡瑁",443,},
