        [4538] = {7805,4,2,"王粲",121,0,"曹公兵强将勇，足智多谋。擒吕布于下邳，摧袁绍于官渡，逐刘备于陇右，破乌桓于白狼，枭除荡定者，不可胜计。","曹公兵强将勇，足智多谋。擒吕布于下邳，摧袁绍于官渡，逐刘备于陇右，破乌桓于白狼，枭除荡定者，不可胜计。",1,"story_wc2_2","story_wc2_2","刘琮",223,},
