        [4548] = {7806,4,1,"刘备",202,0,"吾兄临危托孤于我，今若执其子而夺其地，异日死于九泉之下，何面目复见吾兄乎？","吾兄临危托孤于我，今若执其子而夺其地，异日死于九泉之下，何面目复见吾兄乎？",1,"story_lb2_264","story_lb2_264","",0,},
