        [4550] = {7807,1,1,"刘备",202,0,"今曹兵已至宛城，何以拒敌？","今曹兵已至宛城，何以拒敌？",1,"story_lb2_265","story_lb2_265","诸葛亮",203,},
        [4551] = {7807,2,2,"诸葛亮",203,0,"不战，走樊城以避之。","不战，走樊城以避之。",1,"story_zgl_85","story_zgl_85","刘备",202,},
        [4552] = {7807,3,1,"刘备",202,0,"就依孔明之计。","就依孔明之计。",1,"story_lb2_266","story_lb2_266","诸葛亮",203,},
        [4553] = {7901,1,1,"刘备",202,0,"曹兵已到博望，孔明快与我商议拒敌之计。","曹兵已到博望，孔明快与我商议拒敌之计。",1,"story_lb2_267","story_lb2_267","诸葛亮",203,},
        [4554] = {7901,2,2,"诸葛亮",203,0,"主公且宽心。前番一把火，烧了夏侯惇大半人马，今番曹军又来，必教他中这条计。","主公且宽心。前番一把火，烧了夏侯惇大半人马，今番曹军又来，必教他中这条计。",1,"story_zgl_86","story_zgl_86","刘备",202,},
        [4555] = {7901,3,1,"刘备",202,0,"何计？","何计？",1,"story_lb2_268","story_lb2_268","诸葛亮",203,},
