        [4557] = {7901,5,2,"诸葛亮",203,0,"还烦请告新野老幼男女，愿从者，即于今日皆跟我往樊城暂避，不可自误。","还烦请告新野老幼男女，愿从者，即于今日皆跟我往樊城暂避，不可自误。",1,"story_zgl_88","story_zgl_88","刘备",202,},
        [4558] = {7902,1,1,"刘备",202,0,"何计治敌？","何计治敌？",1,"story_lb2_269","story_lb2_269","诸葛亮",203,},
        [4559] = {7902,2,2,"诸葛亮",203,0,"孙乾往河边调拨船只，救济百姓，糜竺护送各官家眷到樊城。云长引一千军去白河上流头埋伏，各带布袋，多装沙土，遏住白河之水。","孙乾往河边调拨船只，救济百姓，糜竺护送各官家眷到樊城。云长引一千军去白河上流头埋伏，各带布袋，多装沙土，遏住白河之水。",1,"story_zgl_89","story_zgl_89","刘备",202,},
        [4560] = {7902,3,2,"诸葛亮",203,0,"孙乾往河边调拨船只，救济百姓，糜竺护送各官家眷到樊城。云长引一千军去白河上流头埋伏，各带布袋，多装沙土，遏住白河之水。","孙乾往河边调拨船只，救济百姓，糜竺护送各官家眷到樊城。云长引一千军去白河上流头埋伏，各带布袋，多装沙土，遏住白河之水。",1,"story_zgl_90","story_zgl_90","刘备",202,},
        [4561] = {7902,4,2,"诸葛亮",203,0,"至来日三更后，只听下流头人喊马嘶，急取起布袋，放水淹之，却顺水杀将下来接应。","至来日三更后，只听下流头人喊马嘶，急取起布袋，放水淹之，却顺水杀将下来接应。",1,"story_zgl_91","story_zgl_91","刘备",202,},
        [4562] = {7902,5,2,"诸葛亮",203,0,"张飞引一千军去博陵渡口埋伏。此处水势最慢，曹军被淹，必从此逃难，可便乘势杀来接应。","张飞引一千军去博陵渡口埋伏。此处水势最慢，曹军被淹，必从此逃难，可便乘势杀来接应。",1,"story_zgl_92","story_zgl_92","刘备",202,},
        [4563] = {7902,6,2,"诸葛亮",203,0,"张飞引一千军去博陵渡口埋伏。此处水势最慢，曹军被淹，必从此逃难，可便乘势杀来接应。","张飞引一千军去博陵渡口埋伏。此处水势最慢，曹军被淹，必从此逃难，可便乘势杀来接应。",1,"story_zgl_93","story_zgl_93","刘备",202,},
        [4564] = {7902,7,2,"诸葛亮",203,0,"赵云引军三千，分为四队，自领一队伏于东门外，其三队分伏西、南、北三门，却先于城内人家屋上，多藏硫黄焰硝引火之物。","赵云引军三千，分为四队，自领一队伏于东门外，其三队分伏西、南、北三门，却先于城内人家屋上，多藏硫黄焰硝引火之物。",1,"story_zgl_94","story_zgl_94","刘备",202,},
        [4565] = {7902,8,2,"诸葛亮",203,0,"赵云引军三千，分为四队，自领一队伏于东门外，其三队分伏西、南、北三门，却先于城内人家屋上，多藏硫黄焰硝引火之物。","赵云引军三千，分为四队，自领一队伏于东门外，其三队分伏西、南、北三门，却先于城内人家屋上，多藏硫黄焰硝引火之物。",1,"story_zgl_95","story_zgl_95","刘备",202,},
        [4566] = {7902,9,2,"诸葛亮",203,0,"曹军入城，必安歇民房。来日黄昏后，必有大风，但看风起，便令西、南、北三门伏军尽将火箭射入城去。","曹军入城，必安歇民房。来日黄昏后，必有大风，但看风起，便令西、南、北三门伏军尽将火箭射入城去。",1,"story_zgl_96","story_zgl_96","刘备",202,},
        [4567] = {7902,10,2,"诸葛亮",203,0,"曹军入城，必安歇民房。来日黄昏后，必有大风，但看风起，便令西、南、北三门伏军尽将火箭射入城去。","曹军入城，必安歇民房。来日黄昏后，必有大风，但看风起，便令西、南、北三门伏军尽将火箭射入城去。",1,"story_zgl_97","story_zgl_97","刘备",202,},
        [4568] = {7902,11,2,"诸葛亮",203,0,"待城中火势大作，却于城外呐喊助威，只留东门放他出走，汝却于东门外从后击之。","待城中火势大作，却于城外呐喊助威，只留东门放他出走，汝却于东门外从后击之。",1,"story_zgl_98","story_zgl_98","刘备",202,},
        [4569] = {7902,12,2,"诸葛亮",203,0,"待城中火势大作，却于城外呐喊助威，只留东门放他出走，汝却于东门外从后击之。","待城中火势大作，却于城外呐喊助威，只留东门放他出走，汝却于东门外从后击之。",1,"story_zgl_99","story_zgl_99","刘备",202,},
        [4570] = {7902,13,2,"诸葛亮",203,0,"天明会合关、张二将，收军回樊城。再令糜芳、刘封二人带二千军。一半红旗，一半青旗，去新野城外三十里鹊尾坡前屯住。","天明会合关、张二将，收军回樊城。再令糜芳、刘封二人带二千军。一半红旗，一半青旗，去新野城外三十里鹊尾坡前屯住。",1,"story_zgl_100","story_zgl_100","刘备",202,},
        [4571] = {7902,14,2,"诸葛亮",203,0,"天明会合关、张二将，收军回樊城。再令糜芳、刘封二人带二千军。一半红旗，一半青旗，去新野城外三十里鹊尾坡前屯住。","天明会合关、张二将，收军回樊城。再令糜芳、刘封二人带二千军。一半红旗，一半青旗，去新野城外三十里鹊尾坡前屯住。",1,"story_zgl_101","story_zgl_101","刘备",202,},
        [4572] = {7902,15,2,"诸葛亮",203,0,"一见曹军到，红旗军走在左，青旗军走在右。他心疑必不敢追。汝二人却去分头埋伏。","一见曹军到，红旗军走在左，青旗军走在右。他心疑必不敢追。汝二人却去分头埋伏。",1,"story_zgl_102","story_zgl_102","刘备",202,},
        [4573] = {7902,16,2,"诸葛亮",203,0,"一见曹军到，红旗军走在左，青旗军走在右。他心疑必不敢追。汝二人却去分头埋伏。","一见曹军到，红旗军走在左，青旗军走在右。他心疑必不敢追。汝二人却去分头埋伏。",1,"story_zgl_103","story_zgl_103","刘备",202,},
        [4574] = {7902,17,2,"诸葛亮",203,0,"只望城中火起，便可追杀败兵，然后却来白河上流头接应。","只望城中火起，便可追杀败兵，然后却来白河上流头接应。",1,"story_zgl_104","story_zgl_104","刘备",202,},
        [4575] = {7902,18,1,"刘备",202,0,"好，各将依计行事。","好，各将依计行事。",1,"story_lb2_270","story_lb2_270","诸葛亮",203,},
        [4576] = {7903,1,2,"许褚",113,0,"鹊尾坡前一簇人马，尽打青、红旗号。","鹊尾坡前一簇人马，尽打青、红旗号。",1,"story_xc_20","story_xc_20","曹仁",106,},
        [4577] = {7903,2,2,"许褚",113,0,"前面必有伏兵。我兵只在此处住下。","前面必有伏兵。我兵只在此处住下。",1,"story_xc_21","story_xc_21","曹仁",106,},
        [4578] = {7903,3,1,"曹仁",106,0,"此是疑兵，必无埋伏。可速进兵。我当催军继至。","此是疑兵，必无埋伏。可速进兵。我当催军继至。",1,"story_cr_26","story_cr_26","许褚",113,},
        [4579] = {7904,1,1,"曹仁",106,0,"城中亦不见一人，竟是一座空城了。","城中亦不见一人，竟是一座空城了。",1,"story_cr_27","story_cr_27","",0,},
        [4580] = {7904,2,1,"曹仁",106,0,"此是势孤计穷，故尽带百姓逃窜去了，我军权且在城安歇，来日平明进兵。","此是势孤计穷，故尽带百姓逃窜去了，我军权且在城安歇，来日平明进兵。",1,"story_cr_28","story_cr_28","",0,},
        [4581] = {7904,3,1,"曹仁",106,0,"各军走乏，都已饥饿，皆去夺房造饭。","各军走乏，都已饥饿，皆去夺房造饭。",1,"story_cr_29","story_cr_29","",0,},
        [4582] = {7905,1,1,"许褚",113,0,"西、南、北三门皆火起。","西、南、北三门皆火起。",1,"story_xc_22","story_xc_22","曹仁",106,},
        [4583] = {7905,2,2,"曹仁",106,0,"东门无火，走东门。","东门无火，走东门。",1,"story_cr_30","story_cr_30","许褚",113,},
        [4584] = {7905,3,1,"赵云",201,0,"曹仁，哪里跑！","曹仁，哪里跑！",1,"story_zy1_24","story_zy1_24","曹仁",106,},
        [4585] = {7905,4,1,"糜芳",133,0,"贼人，哪里跑！","贼人，哪里跑！",1,"story_mf_1","story_mf_1","曹仁",106,},
        [4586] = {7905,5,1,"张飞",206,0,"曹贼快拿命来！","曹贼快拿命来！",1,"story_zf1_58","story_zf1_58","曹仁",106,},
        [4587] = {7906,1,2,"曹仁",106,0,"丞相，此役失败尔等有过！","丞相，此役失败尔等有过！",1,"story_cr_31","story_cr_31","曹操",103,},
        [4588] = {7906,2,1,"曹操",103,0,"胜负乃兵家常事，不必放在心上。","胜负乃兵家常事，不必放在心上。",1,"story_cc1_499","story_cc1_499","曹仁",106,},
        [4589] = {7906,3,1,"曹操",103,0,"诸葛村夫，安敢如此！","诸葛村夫，安敢如此！",1,"story_cc1_500","story_cc1_500","曹仁",106,},
        [4590] = {7906,4,1,"曹操",103,0,"传令三军，漫山塞野，尽至新野下寨。一面搜山，一面填塞白河。","传令三军，漫山塞野，尽至新野下寨。一面搜山，一面填塞白河。",1,"story_cc1_501","story_cc1_501","曹仁",106,},
        [4591] = {7906,5,1,"曹操",103,0,"大军分作八路，一齐去取樊城。","大军分作八路，一齐去取樊城。",1,"story_cc1_502","story_cc1_502","曹仁",106,},
        [4592] = {8001,1,2,"刘晔",121,0,"丞相初至襄阳，必须先买民心，今刘备尽迁新野百姓入樊城，若我兵径进，二县为齑粉矣，不如先使人招降刘备。","丞相初至襄阳，必须先买民心，今刘备尽迁新野百姓入樊城，若我兵径进，二县为齑粉矣，不如先使人招降刘备。",1,"story_ly2_1","story_ly2_1","曹操",103,},
        [4593] = {8001,2,2,"刘晔",121,0,"丞相初至襄阳，必须先买民心，今刘备尽迁新野百姓入樊城，若我兵径进，二县为齑粉矣，不如先使人招降刘备。","丞相初至襄阳，必须先买民心，今刘备尽迁新野百姓入樊城，若我兵径进，二县为齑粉矣，不如先使人招降刘备。",1,"story_ly2_2","story_ly2_2","曹操",103,},
        [4594] = {8001,3,2,"刘晔",121,0,"备即不降，亦可见我爱民之心，若其来降，则荆州之地，可不战而定也。","备即不降，亦可见我爱民之心，若其来降，则荆州之地，可不战而定也。",1,"story_ly2_3","story_ly2_3","曹操",103,},
        [4595] = {8001,4,1,"曹操",103,0,"谁可为使？","谁可为使？",1,"story_cc1_503","story_cc1_503","刘晔",121,},
        [4596] = {8001,5,2,"刘晔",121,0,"徐庶与刘备至厚，今现在军中，何不命他一往？","徐庶与刘备至厚，今现在军中，何不命他一往？",1,"story_ly2_4","story_ly2_4","曹操",103,},
        [4597] = {8001,6,1,"曹操",103,0,"他去恐不复来。","他去恐不复来。",1,"story_cc1_504","story_cc1_504","刘晔",121,},
        [4598] = {8001,7,2,"刘晔",121,0,"他若不来，贻笑于人矣，丞相勿疑。","他若不来，贻笑于人矣，丞相勿疑。",1,"story_ly2_5","story_ly2_5","曹操",103,},
        [4599] = {8002,1,1,"曹操",103,0,"我本欲踏平樊城，奈怜众百姓之命。公可往说刘备，如肯来降，免罪赐爵，若更执迷，军民共戮，玉石俱焚。","我本欲踏平樊城，奈怜众百姓之命。公可往说刘备，如肯来降，免罪赐爵，若更执迷，军民共戮，玉石俱焚。",1,"story_cc1_505","story_cc1_505","徐庶",212,},
        [4600] = {8002,2,1,"曹操",103,0,"我本欲踏平樊城，奈怜众百姓之命。公可往说刘备，如肯来降，免罪赐爵，若更执迷，军民共戮，玉石俱焚。","我本欲踏平樊城，奈怜众百姓之命。公可往说刘备，如肯来降，免罪赐爵，若更执迷，军民共戮，玉石俱焚。",1,"story_cc1_506","story_cc1_506","徐庶",212,},
        [4601] = {8002,3,1,"曹操",103,0,"吾知公忠义，故特使公往。愿勿相负。","吾知公忠义，故特使公往。愿勿相负。",1,"story_cc1_507","story_cc1_507","徐庶",212,},
        [4602] = {8002,4,2,"徐庶",212,0,"吾愿往。","吾愿往。",1,"story_xs1_73","story_xs1_73","曹操",103,},
        [4603] = {8003,1,2,"徐庶",212,0,"曹操使庶来招降使君，乃假买民心也，今彼分兵八路，填白河而进。樊城恐不可守，宜速作行计。","曹操使庶来招降使君，乃假买民心也，今彼分兵八路，填白河而进。樊城恐不可守，宜速作行计。",1,"story_xs1_74","story_xs1_74","刘备",202,},
        [4604] = {8003,2,2,"徐庶",212,0,"曹操使庶来招降使君，乃假买民心也，今彼分兵八路，填白河而进。樊城恐不可守，宜速作行计。","曹操使庶来招降使君，乃假买民心也，今彼分兵八路，填白河而进。樊城恐不可守，宜速作行计。",1,"story_xs1_75","story_xs1_75","刘备",202,},
        [4605] = {8003,3,1,"刘备",202,0,"君即来，何须回。","君即来，何须回。",1,"story_lb2_271","story_lb2_271","徐庶",212,},
