        [4690] = {8111,7,2,"糜夫人",228,0,"不可！将军岂可无马！此子全赖将军保护。妾已重伤，死何足惜！","不可！将军岂可无马！此子全赖将军保护。妾已重伤，死何足惜！",1,"story_mfr_4","story_mfr_4","赵云",201,},
