        [4713] = {8203,1,2,"乐进",116,0,"敌将张飞倒竖虎须，圆睁环眼，手绰蛇矛，立马长板桥上！","敌将张飞倒竖虎须，圆睁环眼，手绰蛇矛，立马长板桥上！",1,"story_yj2_2","story_yj2_2","曹操",103,},
