        [4715] = {8203,3,1,"曹操",103,0,"孤曾闻云长言：翼德于百万军中，取上将之首，如探囊取物。","孤曾闻云长言：翼德于百万军中，取上将之首，如探囊取物。",1,"story_cc1_516","story_cc1_516","乐进",113,},
