        [4729] = {8208,1,1,"曹操",103,0,"今刘备釜中之鱼，阱中之虎，若不就此时擒捉，如放鱼入海，纵虎归山矣。","今刘备釜中之鱼，阱中之虎，若不就此时擒捉，如放鱼入海，纵虎归山矣。",1,"story_cc1_523","story_cc1_523","",0,},
