        [4735] = {8301,1,1,"曹操",103,0,"今刘备已投江夏，恐结连东吴，是滋蔓也，当用何计破之？","今刘备已投江夏，恐结连东吴，是滋蔓也，当用何计破之？",1,"story_cc1_527","story_cc1_527","",0,},
        [4736] = {8301,2,2,"荀攸",120,0,"我今大振兵威，遣使驰檄江东，请孙权会猎于江夏，共擒刘备，分荆州之地，永结盟好。","我今大振兵威，遣使驰檄江东，请孙权会猎于江夏，共擒刘备，分荆州之地，永结盟好。",1,"story_xy3_19","story_xy3_19","曹操",103,},
        [4737] = {8301,3,2,"荀攸",120,0,"孙权必惊疑而来降，则吾事济矣。","孙权必惊疑而来降，则吾事济矣。",1,"story_xy3_20","story_xy3_20","曹操",103,},
