        [4739] = {8302,1,2,"鲁肃",319,0,"荆州与国邻接，江山险固，士民殷富。吾若据而有之，此帝王之资也。","荆州与国邻接，江山险固，士民殷富。吾若据而有之，此帝王之资也。",1,"story_ls1_22","story_ls1_22","孙权",306,},
        [4740] = {8302,2,2,"鲁肃",319,0,"今刘表新亡，刘备新败，肃请奉命往江夏吊丧，因说刘备使抚刘表众将，同心一意，共破曹操。","今刘表新亡，刘备新败，肃请奉命往江夏吊丧，因说刘备使抚刘表众将，同心一意，共破曹操。",1,"story_ls1_23","story_ls1_23","孙权",306,},
        [4741] = {8302,3,2,"鲁肃",319,0,"今刘表新亡，刘备新败，肃请奉命往江夏吊丧，因说刘备使抚刘表众将，同心一意，共破曹操。","今刘表新亡，刘备新败，肃请奉命往江夏吊丧，因说刘备使抚刘表众将，同心一意，共破曹操。",1,"story_ls1_24","story_ls1_24","孙权",306,},
