        [4744] = {8303,1,2,"刘备",202,0,"曹军今又星夜兼道取江陵，今后该如何应对？","曹军今又星夜兼道取江陵，今后该如何应对？",1,"story_lb2_292","story_lb2_292","诸葛亮",203,},
