        [4750] = {8303,7,1,"诸葛亮",203,0,"若南军胜，共诛曹操以取荆州之地，若北军胜，则我乘势以取江南可也。","若南军胜，共诛曹操以取荆州之地，若北军胜，则我乘势以取江南可也。",1,"story_zgl_110","story_zgl_110","刘备",202,},
