        [4901] = {8404,3,1,"周瑜",303,0,"曹操以天子为名，其师不可拒。且其势大，未可轻敌。战则必败，降则易安。吾意已决。来日见主公，便当遣使纳降。","曹操以天子为名，其师不可拒。且其势大，未可轻敌。战则必败，降则易安。吾意已决。来日见主公，便当遣使纳降。",1,"story_zy2_65","story_zy2_65","鲁肃",319,},
