        [4924] = {8406,2,1,"诸葛亮",203,0,"亮居隆中时，即闻操于漳河新造一台，名曰铜雀，极其壮丽，广选天下美女以实其中。","亮居隆中时，即闻操于漳河新造一台，名曰铜雀，极其壮丽，广选天下美女以实其中。",1,"story_zgl_160","story_zgl_160","周瑜",303,},
