        [4926] = {8406,4,1,"诸葛亮",203,0,"操本好色之徒，久闻江东乔公有二女，长曰大乔，次曰小乔，有沉鱼落雁之容，闭月羞花之貌。","操本好色之徒，久闻江东乔公有二女，长曰大乔，次曰小乔，有沉鱼落雁之容，闭月羞花之貌。",1,"story_zgl_162","story_zgl_162","周瑜",303,},
        [4927] = {8406,5,1,"诸葛亮",203,0,"操曾发誓，一愿扫平四海，以成帝业，一愿得江东二乔，置之铜雀台，以乐晚年，虽死无恨矣。","操曾发誓，一愿扫平四海，以成帝业，一愿得江东二乔，置之铜雀台，以乐晚年，虽死无恨矣。",1,"story_zgl_163","story_zgl_163","周瑜",303,},
        [4928] = {8406,6,1,"诸葛亮",203,0,"操曾发誓，一愿扫平四海，以成帝业，一愿得江东二乔，置之铜雀台，以乐晚年，虽死无恨矣。","操曾发誓，一愿扫平四海，以成帝业，一愿得江东二乔，置之铜雀台，以乐晚年，虽死无恨矣。",1,"story_zgl_164","story_zgl_164","周瑜",303,},
        [4929] = {8406,7,1,"诸葛亮",203,0,"今虽引百万之众，虎视江南，其实为此二女也。将军何不去寻乔公，以千金买此二女，差人送与曹操。","今虽引百万之众，虎视江南，其实为此二女也。将军何不去寻乔公，以千金买此二女，差人送与曹操。",1,"story_zgl_165","story_zgl_165","周瑜",303,},
        [4930] = {8406,8,1,"诸葛亮",203,0,"今虽引百万之众，虎视江南，其实为此二女也。将军何不去寻乔公，以千金买此二女，差人送与曹操。","今虽引百万之众，虎视江南，其实为此二女也。将军何不去寻乔公，以千金买此二女，差人送与曹操。",1,"story_zgl_166","story_zgl_166","周瑜",303,},
        [4931] = {8406,9,1,"诸葛亮",203,0,"此范蠡献西施之计，操得二女，称心满意，必班师矣。","此范蠡献西施之计，操得二女，称心满意，必班师矣。",1,"story_zgl_167","story_zgl_167","周瑜",303,},
