        [4933] = {8406,11,1,"主角",1,0,"曹操幼子曹植，字子建，下笔成文。操尝命作一赋，名曰《铜雀台赋》。赋中之意，单道他家合为天子，誓取二乔。","曹操幼子曹植，字子建，下笔成文。操尝命作一赋，名曰《铜雀台赋》。赋中之意，单道他家合为天子，誓取二乔。",1,"story_zj2_496","story_nzj2_496","周瑜",303,},
        [4934] = {8406,12,1,"主角",1,0,"曹操幼子曹植，字子建，下笔成文。操尝命作一赋，名曰《铜雀台赋》。赋中之意，单道他家合为天子，誓取二乔。","曹操幼子曹植，字子建，下笔成文。操尝命作一赋，名曰《铜雀台赋》。赋中之意，单道他家合为天子，誓取二乔。",1,"story_zj2_497","story_nzj2_497","周瑜",303,},
