        [4947] = {8407,6,2,"张昭",315,0,"曹操挟天子而征四方，动以朝廷为名，近又得荆州，威势越大。吾江东可以拒操者，长江耳。","曹操挟天子而征四方，动以朝廷为名，近又得荆州，威势越大。吾江东可以拒操者，长江耳。",1,"story_zz_46","story_zz_46","周瑜",303,},
        [4948] = {8407,7,2,"张昭",315,0,"今操艨艟战舰，何止千百？水陆并进，何可当之？不如且降，更图后计。","今操艨艟战舰，何止千百？水陆并进，何可当之？不如且降，更图后计。",1,"story_zz_47","story_zz_47","周瑜",303,},
        [4949] = {8407,8,2,"张昭",315,0,"今操艨艟战舰，何止千百？水陆并进，何可当之？不如且降，更图后计。","今操艨艟战舰，何止千百？水陆并进，何可当之？不如且降，更图后计。",1,"story_zz_48","story_zz_48","周瑜",303,},
