        [4952] = {8407,11,1,"周瑜",303,0,"操虽托名汉相，实为汉贼。将军以神武雄才，仗父兄余业，据有江东，兵精粮足，为国家除残去暴，奈何降贼耶？","操虽托名汉相，实为汉贼。将军以神武雄才，仗父兄余业，据有江东，兵精粮足，为国家除残去暴，奈何降贼耶？",1,"story_zy2_78","story_zy2_78","孙权",306,},
        [4953] = {8407,12,1,"周瑜",303,0,"操虽托名汉相，实为汉贼。将军以神武雄才，仗父兄余业，据有江东，兵精粮足，为国家除残去暴，奈何降贼耶？","操虽托名汉相，实为汉贼。将军以神武雄才，仗父兄余业，据有江东，兵精粮足，为国家除残去暴，奈何降贼耶？",1,"story_zy2_79","story_zy2_79","孙权",306,},
        [4954] = {8407,13,1,"周瑜",303,0,"且操今此来，北土未平，久于南征，一忌也，北军不熟水战，操舍鞍马，仗舟楫，与东吴争衡，二忌也。","且操今此来，北土未平，久于南征，一忌也，北军不熟水战，操舍鞍马，仗舟楫，与东吴争衡，二忌也。",1,"story_zy2_80","story_zy2_80","孙权",306,},
        [4955] = {8407,14,1,"周瑜",303,0,"且操今此来，北土未平，久于南征，一忌也，北军不熟水战，操舍鞍马，仗舟楫，与东吴争衡，二忌也。","且操今此来，北土未平，久于南征，一忌也，北军不熟水战，操舍鞍马，仗舟楫，与东吴争衡，二忌也。",1,"story_zy2_81","story_zy2_81","孙权",306,},
        [4956] = {8407,15,1,"周瑜",303,0,"又时值隆冬盛寒，马无藁草，三忌也，远涉江湖，不服水土，多生疾病，四忌也。","又时值隆冬盛寒，马无藁草，三忌也，远涉江湖，不服水土，多生疾病，四忌也。",1,"story_zy2_82","story_zy2_82","孙权",306,},
        [4957] = {8407,16,1,"周瑜",303,0,"操兵犯此数忌，虽多必败。将军擒操，正在今日。瑜请得精兵数万人，进屯夏口，为将军破之！","操兵犯此数忌，虽多必败。将军擒操，正在今日。瑜请得精兵数万人，进屯夏口，为将军破之！",1,"story_zy2_83","story_zy2_83","孙权",306,},
        [4958] = {8407,17,1,"周瑜",303,0,"操兵犯此数忌，虽多必败。将军擒操，正在今日。瑜请得精兵数万人，进屯夏口，为将军破之！","操兵犯此数忌，虽多必败。将军擒操，正在今日。瑜请得精兵数万人，进屯夏口，为将军破之！",1,"story_zy2_84","story_zy2_84","孙权",306,},
        [4959] = {8407,18,2,"孙权",306,0,"封汝为大都督，程普为副都督，鲁肃为赞军校尉，起兵伐曹。诸官将有再言降操者，斩！","封汝为大都督，程普为副都督，鲁肃为赞军校尉，起兵伐曹。诸官将有再言降操者，斩！",1,"story_sq1_129","story_sq1_129","周瑜",303,},
        [4960] = {8407,19,2,"孙权",306,0,"封汝为大都督，程普为副都督，鲁肃为赞军校尉，起兵伐曹。诸官将有再言降操者，斩！","封汝为大都督，程普为副都督，鲁肃为赞军校尉，起兵伐曹。诸官将有再言降操者，斩！",1,"story_sq1_130","story_sq1_130","周瑜",303,},
