        [4964] = {8501,4,1,"诸葛亮",203,0,"心怯曹兵之多，怀寡不敌众之意。将军能以军数开解，使其了然无疑，然后大事可成。","心怯曹兵之多，怀寡不敌众之意。将军能以军数开解，使其了然无疑，然后大事可成。",1,"story_zgl_172","story_zgl_172","周瑜",303,},
