        [4970] = {8502,4,1,"周瑜",303,0,"瑜特为此来开解主公。主公因见操檄文，言水陆大军百万，故怀疑惧，不复料其虚实。","瑜特为此来开解主公。主公因见操檄文，言水陆大军百万，故怀疑惧，不复料其虚实。",1,"story_zy2_89","story_zy2_89","孙权",306,},
        [4971] = {8502,5,1,"周瑜",303,0,"瑜特为此来开解主公。主公因见操檄文，言水陆大军百万，故怀疑惧，不复料其虚实。","瑜特为此来开解主公。主公因见操檄文，言水陆大军百万，故怀疑惧，不复料其虚实。",1,"story_zy2_90","story_zy2_90","孙权",306,},
        [4972] = {8502,6,1,"周瑜",303,0,"然，夫以久疲之卒，御狐疑之众，其数虽多，不足畏也。瑜得五万兵，自足破之。愿主公勿以为虑。","然，夫以久疲之卒，御狐疑之众，其数虽多，不足畏也。瑜得五万兵，自足破之。愿主公勿以为虑。",1,"story_zy2_91","story_zy2_91","孙权",306,},
        [4973] = {8502,7,1,"周瑜",303,0,"然，夫以久疲之卒，御狐疑之众，其数虽多，不足畏也。瑜得五万兵，自足破之。愿主公勿以为虑。","然，夫以久疲之卒，御狐疑之众，其数虽多，不足畏也。瑜得五万兵，自足破之。愿主公勿以为虑。",1,"story_zy2_92","story_zy2_92","孙权",306,},
        [4974] = {8502,8,2,"孙权",306,0,"公瑾此言，足释吾疑。子布无谋，深失孤望，独卿及子敬，与孤同心耳。","公瑾此言，足释吾疑。子布无谋，深失孤望，独卿及子敬，与孤同心耳。",1,"story_sq1_133","story_sq1_133","周瑜",303,},
        [4975] = {8503,1,1,"周瑜",303,0,"孔明早已料着吴侯之心。其计画又高我一头。久必为江东之患，不如杀之。","孔明早已料着吴侯之心。其计画又高我一头。久必为江东之患，不如杀之。",1,"story_zy2_93","story_zy2_93","鲁肃",319,},
        [4976] = {8503,2,1,"周瑜",303,0,"孔明早已料着吴侯之心。其计画又高我一头。久必为江东之患，不如杀之。","孔明早已料着吴侯之心。其计画又高我一头。久必为江东之患，不如杀之。",1,"story_zy2_94","story_zy2_94","鲁肃",319,},
