        [4995] = {8506,1,2,"周瑜",303,0,"昔曹操兵少，袁绍兵多，而操反胜绍者，因用许攸之谋，先断乌巢之粮也。","昔曹操兵少，袁绍兵多，而操反胜绍者，因用许攸之谋，先断乌巢之粮也。",1,"story_zy2_100","story_zy2_100","诸葛亮",203,},
        [4996] = {8506,2,2,"周瑜",303,0,"今操兵八十三万，我兵只五六万，安能拒之？亦必须先断操之粮，然后可破。我已探知操军粮草，俱屯于聚铁山。","今操兵八十三万，我兵只五六万，安能拒之？亦必须先断操之粮，然后可破。我已探知操军粮草，俱屯于聚铁山。",1,"story_zy2_101","story_zy2_101","诸葛亮",203,},
        [4997] = {8506,3,2,"周瑜",303,0,"今操兵八十三万，我兵只五六万，安能拒之？亦必须先断操之粮，然后可破。我已探知操军粮草，俱屯于聚铁山。","今操兵八十三万，我兵只五六万，安能拒之？亦必须先断操之粮，然后可破。我已探知操军粮草，俱屯于聚铁山。",1,"story_zy2_102","story_zy2_102","诸葛亮",203,},
        [4998] = {8506,4,2,"周瑜",303,0,"先生久居汉上，熟知地理。敢烦先生星夜往聚铁山断操粮道。彼此各为主人之事，幸勿推调。","先生久居汉上，熟知地理。敢烦先生星夜往聚铁山断操粮道。彼此各为主人之事，幸勿推调。",1,"story_zy2_103","story_zy2_103","诸葛亮",203,},
