        [5008] = {8508,5,1,"诸葛亮",203,0,"江南小儿谣言云，伏路把关饶子敬，临江水战有周郎。","江南小儿谣言云，伏路把关饶子敬，临江水战有周郎。",1,"story_zgl_183","story_zgl_183","鲁肃",319,},
