        [5188] = {8801,4,2,"荀攸",120,0,"蔡瑁被诛，蔡氏宗族，皆在军中。瑁之族弟蔡中、蔡和现为副将。丞相可以恩结之，差往诈降东吴，必不见疑。","蔡瑁被诛，蔡氏宗族，皆在军中。瑁之族弟蔡中、蔡和现为副将。丞相可以恩结之，差往诈降东吴，必不见疑。",1,"story_xy3_23","story_xy3_23","曹操",103,},
        [5189] = {8801,5,2,"荀攸",120,0,"蔡瑁被诛，蔡氏宗族，皆在军中。瑁之族弟蔡中、蔡和现为副将。丞相可以恩结之，差往诈降东吴，必不见疑。","蔡瑁被诛，蔡氏宗族，皆在军中。瑁之族弟蔡中、蔡和现为副将。丞相可以恩结之，差往诈降东吴，必不见疑。",1,"story_xy3_24","story_xy3_24","曹操",103,},
        [5190] = {8801,6,1,"曹操",103,0,"好计。","好计。",1,"story_cc1_554","story_cc1_554","荀攸",120,},
        [5191] = {8802,1,1,"曹操",103,0,"蔡中、蔡和，汝二人可引些少军士，去东吴诈降。但有动静，使人密报，事成之后，重加封赏。休怀二心！","蔡中、蔡和，汝二人可引些少军士，去东吴诈降。但有动静，使人密报，事成之后，重加封赏。休怀二心！",1,"story_cc1_555","story_cc1_555","蔡和",138,},
        [5192] = {8802,2,1,"曹操",103,0,"蔡中、蔡和，汝二人可引些少军士，去东吴诈降。但有动静，使人密报，事成之后，重加封赏。休怀二心！","蔡中、蔡和，汝二人可引些少军士，去东吴诈降。但有动静，使人密报，事成之后，重加封赏。休怀二心！",1,"story_cc1_556","story_cc1_556","蔡和",138,},
        [5193] = {8802,3,2,"蔡和",138,0,"吾等妻子俱在荆州，安敢怀二心，丞相勿疑。某二人必取周瑜、诸葛亮之首，献于麾下。","吾等妻子俱在荆州，安敢怀二心，丞相勿疑。某二人必取周瑜、诸葛亮之首，献于麾下。",1,"story_ch2_1","story_ch2_1","曹操",103,},
        [5194] = {8802,4,1,"曹操",103,0,"好，孤现在就厚赏汝二人。","好，孤现在就厚赏汝二人。",1,"story_cc1_557","story_cc1_557","蔡和",138,},
