        [5466] = {9301,1,1,"诸葛亮",203,0,"子龙可带三千军马，渡江径取乌林小路，拣树木芦苇密处埋伏。今夜四更已后，曹操必然从那条路奔走。","子龙可带三千军马，渡江径取乌林小路，拣树木芦苇密处埋伏。今夜四更已后，曹操必然从那条路奔走。",1,"story_zgl_239","story_zgl_239","赵云",201,},
        [5467] = {9301,2,1,"诸葛亮",203,0,"子龙可带三千军马，渡江径取乌林小路，拣树木芦苇密处埋伏。今夜四更已后，曹操必然从那条路奔走。","子龙可带三千军马，渡江径取乌林小路，拣树木芦苇密处埋伏。今夜四更已后，曹操必然从那条路奔走。",1,"story_zgl_240","story_zgl_240","赵云",201,},
        [5468] = {9301,3,1,"诸葛亮",203,0,"等他军马过，就半中间放起火来。虽然不杀他尽绝，也杀一半。","等他军马过，就半中间放起火来。虽然不杀他尽绝，也杀一半。",1,"story_zgl_241","story_zgl_241","赵云",201,},
        [5469] = {9301,4,2,"赵云",201,0,"乌林有两条路：一条通南郡，一条取荆州。不知向那条路来？","乌林有两条路：一条通南郡，一条取荆州。不知向那条路来？",1,"story_zy1_54","story_zy1_54","诸葛亮",203,},
