        [5484] = {9302,1,1,"关羽",205,0,"关某自随兄长征战，许多年来，未尝落后。今日逢大敌，军师却不委用，此是何意？","关某自随兄长征战，许多年来，未尝落后。今日逢大敌，军师却不委用，此是何意？",1,"story_gy1_111","story_gy1_111","诸葛亮",203,},
