        [5488] = {9302,5,2,"诸葛亮",203,0,"昔日曹操待足下甚厚，足下当有以报之。今日操兵败，必走华容道，若令足下去时，必然放他过去。因此不敢教去。","昔日曹操待足下甚厚，足下当有以报之。今日操兵败，必走华容道，若令足下去时，必然放他过去。因此不敢教去。",1,"story_zgl_252","story_zgl_252","关羽",205,},
        [5489] = {9302,6,2,"诸葛亮",203,0,"昔日曹操待足下甚厚，足下当有以报之。今日操兵败，必走华容道，若令足下去时，必然放他过去。因此不敢教去。","昔日曹操待足下甚厚，足下当有以报之。今日操兵败，必走华容道，若令足下去时，必然放他过去。因此不敢教去。",1,"story_zgl_253","story_zgl_253","关羽",205,},
