        [5523] = {9403,8,1,"曹操",103,0,"诸葛亮多谋，故使人于山僻烧烟，使我军不敢从这条山路走，他却伏兵于大路等着。吾料已定，偏不教中他计！","诸葛亮多谋，故使人于山僻烧烟，使我军不敢从这条山路走，他却伏兵于大路等着。吾料已定，偏不教中他计！",1,"story_cc1_611","story_cc1_611","张辽",109,},
        [5524] = {9403,9,1,"曹操",103,0,"诸葛亮多谋，故使人于山僻烧烟，使我军不敢从这条山路走，他却伏兵于大路等着。吾料已定，偏不教中他计！","诸葛亮多谋，故使人于山僻烧烟，使我军不敢从这条山路走，他却伏兵于大路等着。吾料已定，偏不教中他计！",1,"story_cc1_612","story_cc1_612","张辽",109,},
