        [5540] = {9501,1,1,"曹操",103,0,"南方献来一只山鸡，山鸡爱其毛羽，看到水里的倒影就会起舞。","南方献来一只山鸡，山鸡爱其毛羽，看到水里的倒影就会起舞。",1,"story_cc1_619","story_cc1_619","",0,},
