        [5562] = {9601,5,1,"刘备",202,0,"胜负不可预定。曹操临归，令曹仁守南郡等处，必有奇计，更兼曹仁勇不可当，但恐都督不能取耳。","胜负不可预定。曹操临归，令曹仁守南郡等处，必有奇计，更兼曹仁勇不可当，但恐都督不能取耳。",1,"story_lb2_317","story_lb2_317","周瑜",303,},
        [5563] = {9601,6,1,"刘备",202,0,"胜负不可预定。曹操临归，令曹仁守南郡等处，必有奇计，更兼曹仁勇不可当，但恐都督不能取耳。","胜负不可预定。曹操临归，令曹仁守南郡等处，必有奇计，更兼曹仁勇不可当，但恐都督不能取耳。",1,"story_lb2_318","story_lb2_318","周瑜",303,},
