        [5567] = {9601,10,1,"刘备",202,0,"都督此言，甚是公论。先让东吴去取，若不下，主公取之，有何不可！","都督此言，甚是公论。先让东吴去取，若不下，主公取之，有何不可！",1,"story_lb2_320","story_lb2_320","周瑜",303,},
        [5568] = {9601,11,1,"刘备",202,0,"都督此言，甚是公论。先让东吴去取，若不下，主公取之，有何不可！","都督此言，甚是公论。先让东吴去取，若不下，主公取之，有何不可！",1,"story_lb2_321","story_lb2_321","周瑜",303,},
        [5569] = {9602,1,1,"刘备",202,0,"却才先生教备如此回答，虽一时说了，展转寻思，于理未然。我今孤穷一身，无置足之地，欲得南郡，权且容身。","却才先生教备如此回答，虽一时说了，展转寻思，于理未然。我今孤穷一身，无置足之地，欲得南郡，权且容身。",1,"story_lb2_322","story_lb2_322","诸葛亮",203,},
        [5570] = {9602,2,1,"刘备",202,0,"却才先生教备如此回答，虽一时说了，展转寻思，于理未然。我今孤穷一身，无置足之地，欲得南郡，权且容身。","却才先生教备如此回答，虽一时说了，展转寻思，于理未然。我今孤穷一身，无置足之地，欲得南郡，权且容身。",1,"story_lb2_323","story_lb2_323","诸葛亮",203,},
