        [5980] = {10804,3,1,"诸葛亮",203,0,"子敬回见吴侯，善言伸意，休生妄想。若不准我文书，我翻了面皮，连八十一州都夺了。","子敬回见吴侯，善言伸意，休生妄想。若不准我文书，我翻了面皮，连八十一州都夺了。",1,"story_zgl_307","story_zgl_307","鲁肃",319,},
        [5981] = {10804,4,1,"诸葛亮",203,0,"子敬回见吴侯，善言伸意，休生妄想。若不准我文书，我翻了面皮，连八十一州都夺了。","子敬回见吴侯，善言伸意，休生妄想。若不准我文书，我翻了面皮，连八十一州都夺了。",1,"story_zgl_308","story_zgl_308","鲁肃",319,},
