        [5985] = {10805,3,1,"周瑜",303,0,"子敬中诸葛之谋也！名为借地，实是混赖。他说取了西川便还，知他几时取西川？","子敬中诸葛之谋也！名为借地，实是混赖。他说取了西川便还，知他几时取西川？",1,"story_zy2_251","story_zy2_251","鲁肃",319,},
        [5986] = {10805,4,1,"周瑜",303,0,"子敬中诸葛之谋也！名为借地，实是混赖。他说取了西川便还，知他几时取西川？","子敬中诸葛之谋也！名为借地，实是混赖。他说取了西川便还，知他几时取西川？",1,"story_zy2_252","story_zy2_252","鲁肃",319,},
        [5987] = {10805,5,1,"周瑜",303,0,"这等文书，如何中用，你却与他做保！他若不还时，必须连累足下，主公见罪奈何？","这等文书，如何中用，你却与他做保！他若不还时，必须连累足下，主公见罪奈何？",1,"story_zy2_253","story_zy2_253","鲁肃",319,},
        [5988] = {10805,6,1,"周瑜",303,0,"这等文书，如何中用，你却与他做保！他若不还时，必须连累足下，主公见罪奈何？","这等文书，如何中用，你却与他做保！他若不还时，必须连累足下，主公见罪奈何？",1,"story_zy2_254","story_zy2_254","鲁肃",319,},
