        [5990] = {10805,8,1,"周瑜",303,0,"子敬乃诚实人也。刘备枭雄之辈，诸葛亮奸猾之徒，恐不似先生心地。","子敬乃诚实人也。刘备枭雄之辈，诸葛亮奸猾之徒，恐不似先生心地。",1,"story_zy2_255","story_zy2_255","鲁肃",319,},
        [5991] = {10901,1,2,"细作",131,0,"荆州城中扬起布幡做好事，城外别建新坟，军士各挂孝。","荆州城中扬起布幡做好事，城外别建新坟，军士各挂孝。",1,"story_xz_1","story_xz_1","周瑜",303,},
        [5992] = {10901,2,1,"周瑜",303,0,"没了甚人？","没了甚人？",1,"story_zy2_256","story_zy2_256","细作",131,},
        [5993] = {10901,3,2,"细作",131,0,"刘玄德没了甘夫人，即日安排殡葬。","刘玄德没了甘夫人，即日安排殡葬。",1,"story_xz_2","story_xz_2","周瑜",303,},
        [5994] = {10901,4,1,"周瑜",303,0,"吾计成矣：使刘备束手就缚，荆州反掌可得！","吾计成矣：使刘备束手就缚，荆州反掌可得！",1,"story_zy2_257","story_zy2_257","细作",131,},
        [5995] = {10901,5,1,"周瑜",303,0,"我今上书主公，教人去荆州为媒，说刘备来入赘。赚到南徐，妻子不能勾得，幽囚在狱中，却使人去讨荆州换刘备。","我今上书主公，教人去荆州为媒，说刘备来入赘。赚到南徐，妻子不能勾得，幽囚在狱中，却使人去讨荆州换刘备。",1,"story_zy2_258","story_zy2_258","细作",131,},
        [5996] = {10901,6,1,"周瑜",303,0,"我今上书主公，教人去荆州为媒，说刘备来入赘。赚到南徐，妻子不能勾得，幽囚在狱中，却使人去讨荆州换刘备。","我今上书主公，教人去荆州为媒，说刘备来入赘。赚到南徐，妻子不能勾得，幽囚在狱中，却使人去讨荆州换刘备。",1,"story_zy2_259","story_zy2_259","细作",131,},
        [5997] = {10901,7,1,"周瑜",303,0,"等他交割了荆州城池，我别有主意。于子敬身上，须无事也。","等他交割了荆州城池，我别有主意。于子敬身上，须无事也。",1,"story_zy2_260","story_zy2_260","细作",131,},
        [5998] = {10902,1,2,"吕范",121,0,"范近闻皇叔失偶，有一门好亲，故不避嫌，特来作媒。未知尊意若何？","范近闻皇叔失偶，有一门好亲，故不避嫌，特来作媒。未知尊意若何？",1,"story_lf_1","story_lf_1","刘备",202,},
        [5999] = {10902,2,2,"吕范",121,0,"范近闻皇叔失偶，有一门好亲，故不避嫌，特来作媒。未知尊意若何？","范近闻皇叔失偶，有一门好亲，故不避嫌，特来作媒。未知尊意若何？",1,"story_lf_2","story_lf_2","刘备",202,},
        [6000] = {10902,3,1,"刘备",202,0,"中年丧妻，大不幸也。骨肉未寒，安忍便议亲？","中年丧妻，大不幸也。骨肉未寒，安忍便议亲？",1,"story_lb2_344","story_lb2_344","吕范",121,},
        [6001] = {10902,4,2,"吕范",121,0,"人若无妻，如屋无梁，岂可中道而废人伦？吾主吴侯有一妹，美而贤，堪奉箕帚。","人若无妻，如屋无梁，岂可中道而废人伦？吾主吴侯有一妹，美而贤，堪奉箕帚。",1,"story_lf_3","story_lf_3","刘备",202,},
        [6002] = {10902,5,2,"吕范",121,0,"人若无妻，如屋无梁，岂可中道而废人伦？吾主吴侯有一妹，美而贤，堪奉箕帚。","人若无妻，如屋无梁，岂可中道而废人伦？吾主吴侯有一妹，美而贤，堪奉箕帚。",1,"story_lf_4","story_lf_4","刘备",202,},
        [6003] = {10902,6,2,"吕范",121,0,"若两家共结秦、晋之好，则曹贼不敢正视东南也。但我国太吴夫人甚爱幼女，不肯远嫁，必求皇叔到东吴就婚。","若两家共结秦、晋之好，则曹贼不敢正视东南也。但我国太吴夫人甚爱幼女，不肯远嫁，必求皇叔到东吴就婚。",1,"story_lf_5","story_lf_5","刘备",202,},
        [6004] = {10902,7,2,"吕范",121,0,"若两家共结秦、晋之好，则曹贼不敢正视东南也。但我国太吴夫人甚爱幼女，不肯远嫁，必求皇叔到东吴就婚。","若两家共结秦、晋之好，则曹贼不敢正视东南也。但我国太吴夫人甚爱幼女，不肯远嫁，必求皇叔到东吴就婚。",1,"story_lf_6","story_lf_6","刘备",202,},
        [6005] = {10902,8,1,"刘备",202,0,"吾年已半百，鬓发斑白；吴侯之妹，正当妙龄：恐非配偶。","吾年已半百，鬓发斑白；吴侯之妹，正当妙龄：恐非配偶。",1,"story_lb2_345","story_lb2_345","吕范",121,},
        [6006] = {10902,9,2,"吕范",121,0,"吴侯之妹，身虽女子，志胜男儿。今皇叔名闻四海，正所谓淑女配君子，岂以年齿上下相嫌乎！","吴侯之妹，身虽女子，志胜男儿。今皇叔名闻四海，正所谓淑女配君子，岂以年齿上下相嫌乎！",1,"story_lf_7","story_lf_7","刘备",202,},
        [6007] = {10902,10,2,"吕范",121,0,"吴侯之妹，身虽女子，志胜男儿。今皇叔名闻四海，正所谓淑女配君子，岂以年齿上下相嫌乎！","吴侯之妹，身虽女子，志胜男儿。今皇叔名闻四海，正所谓淑女配君子，岂以年齿上下相嫌乎！",1,"story_lf_8","story_lf_8","刘备",202,},
        [6008] = {10902,11,1,"刘备",202,0,"公且少留，来日回报。","公且少留，来日回报。",1,"story_lb2_346","story_lb2_346","吕范",121,},
        [6009] = {10903,1,1,"诸葛亮",203,0,"他们的来意我已经知道了。刚才算了一卦，大吉大利。主公可以应允这门亲事，择日便去就亲。","他们的来意我已经知道了。刚才算了一卦，大吉大利。主公可以应允这门亲事，择日便去就亲。",1,"story_zgl_310","story_zgl_310","刘备",202,},
        [6010] = {10903,2,1,"诸葛亮",203,0,"他们的来意我已经知道了。刚才算了一卦，大吉大利。主公可以应允这门亲事，择日便去就亲。","他们的来意我已经知道了。刚才算了一卦，大吉大利。主公可以应允这门亲事，择日便去就亲。",1,"story_zgl_311","story_zgl_311","刘备",202,},
        [6011] = {10903,3,2,"刘备",202,0,"周瑜定计欲害刘备，岂可以身轻入危险之地？","周瑜定计欲害刘备，岂可以身轻入危险之地？",1,"story_lb2_347","story_lb2_347","诸葛亮",203,},
