        [6014] = {10904,2,1,"刘备",202,0,"只恐有诈，不敢轻往。","只恐有诈，不敢轻往。",1,"story_lb2_348","story_lb2_348","孙乾",140,},
        [6015] = {10904,3,2,"诸葛亮",203,0,"吾已定下三条计策，非子龙不可行也。","吾已定下三条计策，非子龙不可行也。",1,"story_zgl_313","story_zgl_313","刘备",202,},
