        [6020] = {10906,1,1,"乔国老",133,0,"令爱已许刘玄德为夫人，今玄德已到，何故相瞒？","令爱已许刘玄德为夫人，今玄德已到，何故相瞒？",1,"story_qgl_1","story_qgl_1","吴国太",322,},
