        [6045] = {10910,3,2,"乔国老",133,0,"玄德有龙凤之姿，天日之表；更兼仁德布于天下：国太得此佳婿，真可庆也！","玄德有龙凤之姿，天日之表；更兼仁德布于天下：国太得此佳婿，真可庆也！",1,"story_qgl_8","story_qgl_8","刘备",202,},
