        [6047] = {10910,5,1,"赵云",201,0,"（悄悄告诉刘备）刚才我在廊下巡视，见房内有刀斧手埋伏，必无好意。可告知国太。","（悄悄告诉刘备）刚才我在廊下巡视，见房内有刀斧手埋伏，必无好意。可告知国太。",1,"story_zy1_77","story_zy1_77","刘备",202,},
