        [6056] = {11001,1,1,"刘备",202,0,"若刘备能勾回荆州，成王霸之业，一剑挥石为两段。如死于此地，剑剁石不开。","若刘备能勾回荆州，成王霸之业，一剑挥石为两段。如死于此地，剑剁石不开。",1,"story_lb2_352","story_lb2_352","",0,},
        [6057] = {11001,2,2,"孙权",306,0,"玄德公如何恨此石？竟将之砍为两半？","玄德公如何恨此石？竟将之砍为两半？",1,"story_sq1_153","story_sq1_153","刘备",202,},
        [6058] = {11001,3,1,"刘备",202,0,"备年近五旬，不能为国家剿除贼党，心常自恨。今蒙国太招为女婿，此平生之际遇也。","备年近五旬，不能为国家剿除贼党，心常自恨。今蒙国太招为女婿，此平生之际遇也。",1,"story_lb2_353","story_lb2_353","孙权",306,},
        [6059] = {11001,4,1,"刘备",202,0,"备年近五旬，不能为国家剿除贼党，心常自恨。今蒙国太招为女婿，此平生之际遇也。","备年近五旬，不能为国家剿除贼党，心常自恨。今蒙国太招为女婿，此平生之际遇也。",1,"story_lb2_354","story_lb2_354","孙权",306,},
        [6060] = {11001,5,1,"刘备",202,0,"恰才问天买卦，如破曹兴汉，砍断此石。今果然如此。","恰才问天买卦，如破曹兴汉，砍断此石。今果然如此。",1,"story_lb2_355","story_lb2_355","孙权",306,},
        [6061] = {11001,6,2,"孙权",306,0,"吾亦问天买卦。若破得曹贼，亦断此石。","吾亦问天买卦。若破得曹贼，亦断此石。",1,"story_sq1_154","story_sq1_154","",0,},
        [6062] = {11001,7,2,"孙权",306,0,"（心里暗暗祝告）若再取得荆州，兴旺东吴，砍石为两半！","（心里暗暗祝告）若再取得荆州，兴旺东吴，砍石为两半！",1,"story_sq1_155","story_sq1_155","",0,},
        [6063] = {11002,1,1,"刘备",202,0,"南人驾船，北人乘马，信有之也。","南人驾船，北人乘马，信有之也。",1,"story_lb2_356","story_lb2_356","孙权",306,},
        [6064] = {11002,2,2,"孙权",306,0,"刘备这话是嘲笑我不擅长骑马么？","刘备这话是嘲笑我不擅长骑马么？",1,"story_sq1_156","story_sq1_156","刘备",202,},
        [6065] = {11002,3,2,"孙权",306,0,"来人！牵马！让刘皇叔看看，南人不能乘马乎？","来人！牵马！让刘皇叔看看，南人不能乘马乎？",1,"story_sq1_157","story_sq1_157","",0,},
