        [6067] = {11003,1,1,"主角",1,0,"看大哥和孙权相处得还挺开心的，之前军师给的锦囊不知何时派上用场。","看大哥和孙权相处得还挺开心的，之前军师给的锦囊不知何时派上用场。",1,"story_zj2_529","story_nzj2_529","赵云",201,},
        [6068] = {11003,2,2,"赵云",201,0,"军师料事如神，此次麻烦能够化解还是多亏这锦囊中的妙计。","军师料事如神，此次麻烦能够化解还是多亏这锦囊中的妙计。",1,"story_zy1_79","story_zy1_79","主角",1,},
        [6069] = {11003,3,2,"赵云",201,0,"军师吩咐每个锦囊都有打开的时机，云谨记在心。我们不可松懈，万事定要小心。","军师吩咐每个锦囊都有打开的时机，云谨记在心。我们不可松懈，万事定要小心。",1,"story_zy1_80","story_zy1_80","主角",1,},
        [6070] = {11003,4,1,"主角",1,0,"子龙说的是。","子龙说的是。",1,"story_zj2_530","story_nzj2_530","赵云",201,},
        [6071] = {11101,1,1,"刘备",202,0,"江左之人，多有要害刘备者，恐不能久居。","江左之人，多有要害刘备者，恐不能久居。",1,"story_lb2_358","story_lb2_358","乔国老",133,},
