        [6073] = {11101,3,2,"吴国太",322,0,"我的女婿，谁敢害他！让他明天就搬到书院暂住，择日完婚。","我的女婿，谁敢害他！让他明天就搬到书院暂住，择日完婚。",1,"story_wgt_30","story_wgt_30","刘备",202,},
        [6074] = {11101,4,1,"刘备",202,0,"只恐赵云在外不便，军士无人约束。","只恐赵云在外不便，军士无人约束。",1,"story_lb2_359","story_lb2_359","吴国太",322,},
        [6075] = {11101,5,2,"吴国太",322,0,"让他们全都搬到府中安歇，休留在馆驿中，免得生事。","让他们全都搬到府中安歇，休留在馆驿中，免得生事。",1,"story_wgt_31","story_wgt_31","刘备",202,},
        [6076] = {11102,1,2,"小乔",304,0,"听说刘皇叔要娶孙尚香为妻？","听说刘皇叔要娶孙尚香为妻？",1,"story_xq_27","story_xq_27","刘备",202,},
        [6077] = {11102,2,1,"刘备",202,0,"刘备不才，有幸得吴国太赏识，愿嫁女儿与我，实是幸事。","刘备不才，有幸得吴国太赏识，愿嫁女儿与我，实是幸事。",1,"story_lb2_360","story_lb2_360","小乔",304,},
        [6078] = {11102,3,2,"小乔",304,0,"尚香身虽女子，志胜男儿，想娶她可不是什么易事。我先来会一会你们！","尚香身虽女子，志胜男儿，想娶她可不是什么易事。我先来会一会你们！",1,"story_xq_28","story_xq_28","刘备",202,},
        [6079] = {11102,4,1,"主角",1,0,"刚好！好久没有活动一下了，小乔妹子要小心哦！","刚好！好久没有活动一下了，小乔妹子要小心哦！",1,"story_zj2_531","story_nzj2_531","小乔",304,},
        [6080] = {11103,1,2,"大乔",302,0,"小乔这么容易就让你们过关了？我这里你们可要小心了！","小乔这么容易就让你们过关了？我这里你们可要小心了！",1,"story_dq_22","story_dq_22","主角",1,},
        [6081] = {11103,2,1,"主角",1,0,"大乔妹妹你最好了，连吴国太都认准了我大哥做她的女婿，你就直接成人之美多好！","大乔妹妹你最好了，连吴国太都认准了我大哥做她的女婿，你就直接成人之美多好！",1,"story_zj2_532","story_nzj2_532","大乔",302,},
        [6082] = {11103,3,2,"大乔",302,0,"尚香妹妹可是我的小姑子，若非真英雄，如何做她的夫婿？","尚香妹妹可是我的小姑子，若非真英雄，如何做她的夫婿？",1,"story_dq_23","story_dq_23","主角",1,},
